import React from "react";

// reactstrap components
import { Card, CardBody, CardTitle, Container, Row, Col } from "reactstrap";



class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = { seconds: 0 , accidents:Math.floor(Math.random() * 1000),type:"ACCIDENTS", name_view:"This Month"};
  }

  tick() {
   var new_name_view=""
    if (this.state.seconds % 3 == 0){
      new_name_view="Today"
    }else{
      if(this.state.seconds % 2 == 0){
        new_name_view="This Week"
      }else{
        new_name_view="This Month"
      }
    }

   this.setState(state => ({
       seconds: state.seconds + 1,
       accidents: Math.floor(Math.random() * 1000),
       type:state.type,
       name_view: new_name_view
    }));
  }

  componentDidMount() {
    this.interval = setInterval(() => this.tick(), 3000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return (
        <CardBody>
          <Row>
            <div className="col">
              <CardTitle
                tag="h5"
                className="text-uppercase text-muted mb-0"
              >
              Accidents
              </CardTitle>
              <span className="h2 font-weight-bold mb-0">
                {this.state.name_view + "\t"}  :{this.state.accidents}
              </span>
            </div>
            <Col className="col-auto">
              <div className="icon icon-shape bg-danger text-white rounded-circle shadow">
                <i className="fas fa-chart-bar" />
              </div>
            </Col>
          </Row>
          <p className="mt-3 mb-0 text-muted text-sm">
            <span className="text-success mr-2">
              <i className="fa fa-arrow-up" /> 3.48%
            </span>{" "}
            <span className="text-nowrap">Since last month</span>
          </p>
        </CardBody>
    );
  }
}

export default Timer;
