import React, { useState } from 'react';
import {
  Carousel,
  CarouselItem, Row, CardTitle, Col, CardBody
} from 'reactstrap';

const items = [
  {
    accidents:Math.floor(Math.random() * 1000),
    type:"ACCIDENTS",
    name_view:"Today",
    variation:(Math.random() * 20 - 10).toFixed(2),
    classname:"fa fa-arrow-up"
  },
  {
    accidents:Math.floor(Math.random() * 1000),
    type:"ACCIDENTS",
    name_view:"Week",
    variation:(Math.random() * 20 - 10).toFixed(2),
    classname:"fa fa-arrow-up"
  },
  {
    accidents:Math.floor(Math.random() * 1000),
    type:"ACCIDENTS",
    name_view:"Month",
    variation:(Math.random() * 20 - 10).toFixed(2),
    classname:"fa fa-arrow-up"
  },
];

const Example = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    if (items[nextIndex].variation > 0){
      items[nextIndex].classname="fa fa-arrow-up"
    }else{
      items[nextIndex].classname="fa fa-arrow-down"
    }
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  const slides = items.map((item) => {
    return (
      <CarouselItem
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
        key={item.src}
      ><CardBody>
          <Row>
            <div className="col">
              <CardTitle
                tag="h5"
                className="text-uppercase text-muted mb-0"
              >
              Accidents
              </CardTitle>
              <span className="h2 font-weight-bold mb-0" id = "teste" >
                {item.name_view + "\t\t"} {item.accidents}
              </span>
            </div>
            <Col className="col-auto">
              <div className="icon icon-shape bg-danger text-white rounded-circle shadow">
                <i className="fas fa-chart-bar" />
              </div>
            </Col>
          </Row>
          <p className="mt-3 mb-0 text-muted text-sm">
            <span id = "span1" className="text-success mr-2">
              <i id = "i1" className={item.classname} /> {item.variation}%
            </span>{" "}
            <span className="text-nowrap">Since last month</span>
          </p>
        </CardBody>
      </CarouselItem>
    );
  });

  return (
    <Carousel
      activeIndex={activeIndex}
      next={next}
      previous={previous}
    >
      {slides}
    </Carousel>
  );
}

export default Example;
